import { Todo } from 'src/app/Todo';
@Component({
  selector: 'app-todo-item',
  templateUrl: './todo-item.component.html',
  styleUrls: ['./todo-item.component.css'],
})
export class TodoItemComponent implements OnInit {
  @Input() todos: Todo;
  constructor() {}
  ngOnInit(): void {}
}
<p>{{ todo.title }}</p>
