import { Component, OnInit } from '@angular/core';
import { Todo } from '../../Todo';

@Component({
  selector: 'app-todos',
  templateUrl: './todos.component.html',
  styleUrls: ['./todos.component.css'],
})
export class TodosComponent implements OnInit {
  localItem!: any;
  todos: Todo[];
  constructor() {
    this.localItem = localStorage.getItem('todos');
    if (this.localItem == null) {
      this.todos = [];
    } else {
      this.todos = JSON.parse(this.localItem);
    }
  }
  ngOnInit(): void {}
  deleteTodo(todo: Todo) {
    console.log(todo);
    const index = this.todos.indexOf(todo);
    this.todos.splice(index, 1);
    localStorage.setItem('todos', JSON.stringify(this.todos));
  }
  addTodo(todo: Todo) {
    console.log(todo);
    this.todos.push(todo);
    console.log('todos', this.todos);
    localStorage.setItem('todos', JSON.stringify(this.todos));
    console.log('todos Localstorage', localStorage.getItem('todos'));
  }
  toggleTodo(todo: Todo) {
   // const index = this.todos.push(todo);

    this.todos[1].active = !this.todos[1].active;
    localStorage.setItem('todos', JSON.stringify(this.todos));
  }
}
<app-add-todo (todoAdd)="addTodo($event)"></app-add-todo>
<h4>Your Todos</h4>
<div *ngIf="this.todos.length === 0; else elseBlock">No Todos To Display!</div>
<ng-template #elseBlock>
  <div *ngFor="let todo of todos; index as i">
    <app-todo-item
      [todo]="todo"
      [i]="i"
      (todoDelete)="deleteTodo($event)"
      (todoCheckbox)="toggleTodo($event)"
    ></app-todo-item>
  </div>
</ng-template>
